import React, { useEffect, useState } from 'react';
import './SearchHotelPage.css';
import { Link } from 'react-router-dom';
import { checkAuthentication, logoutUser } from './auth';
import { useNavigate } from 'react-router-dom';

const SearchHotelPage = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [formData, setFormData] = useState({
    city: "",
    checkIn: "",
    checkOut: "",
    adults: 1,
    rooms: 1,
    rating: 0
  });
  const [error, setError] = useState("");
  const [hotels, setHotels] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [searched, setSearched] = useState(false);

  useEffect(() => {
    const fetchAuthStatus = async () => {
      const userEmail = await checkAuthentication();
      if (userEmail) {
        setIsAuthenticated(true);
      }
    };
    fetchAuthStatus();
  }, []);

  const handleLogout = async () => {
    const success = await logoutUser();
    if (success) {
      setIsAuthenticated(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleHotelSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSearched(true);

    for (const key in formData) {
      if (formData[key] === "" || formData[key] === null || formData[key] === undefined) {
        setError("All fields are required.");
        return;
      }
    }

    const hotelSearchData = {
      city: formData.city,
      checkIn: formData.checkIn,
      checkOut: formData.checkOut,
      adults: formData.adults,
      rooms: formData.rooms,
      rating: formData.rating
    };

    try {
      setIsLoading(true);
      const response = await fetch("http://localhost:3001/searchHotels", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(hotelSearchData),
      });

      if (response.ok) {
        const data = await response.json();
        setHotels(data || []);
      } else {
        const errorData = await response.json();
        console.error("Backend Error:", errorData);
        setError(errorData.error || "Error searching hotels");
      }
    } catch (err) {
      console.error("Fetch Error:", err);
      setError("Error searching hotels");
    } finally {
      setIsLoading(false);
    }
  };

  const handleSaveHotel = async (hotelInfo) => {
    try {
      const response = await fetch("http://localhost:3001/saveHotels", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ hotels: [hotelInfo] }), // Save a single hotel at a time
      });

      if (response.ok) {
        const data = await response.json();
        alert(data.message);
      } else {
        const errorData = await response.json();
        console.error("Backend Error:", errorData);
        setError(errorData.error || "Error saving hotel");
      }
    } catch (err) {
      console.error("Fetch Error:", err);
      setError("Error saving hotel");
    }
  };

  const navigate = useNavigate();

  return (
    <div className="search-page">
      <header>
        <div className="header-content">
          <Link to="/flight">
            <button className="btn btn-secondary">Search flights</button>
          </Link>
          {isAuthenticated && (
            <button className="btn btn-secondary" onClick={() => navigate('/profile')}>My Profile</button>
          )}
          {isAuthenticated ? (
            <button className="btn btn-secondary" onClick={handleLogout}>Logout</button>
          ) : (
            <>
              <Link to="/login">
                <button className="btn btn-secondary">Login</button>
              </Link>
              <Link to="/registration">
                <button className="btn btn-secondary">Register</button>
              </Link>
            </>
          )}
        </div>
      </header>

      <h2>Please enter information to search hotels</h2>
      <form onSubmit={handleHotelSubmit} className="form-container">
        <div>
          <label> City:</label>
          <input
            type="text"
            name="city"
            value={formData.city}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Check in (YYYY-MM-DD):</label>
          <input
            type="date"
            name="checkIn"
            value={formData.checkIn}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Check out (YYYY-MM-DD):</label>
          <input
            type="date"
            name="checkOut"
            value={formData.checkOut}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Number of Adults:</label>
          <input
            type="number"
            name="adults"
            value={formData.adults}
            onChange={handleChange}
            min="1"
            required
          />
        </div>
        <div>
          <label>Number of Rooms:</label>
          <input
            type="number"
            name="rooms"
            value={formData.rooms}
            onChange={handleChange}
            min="1"
            required
          />
        </div>
        <div>
          <label>Rating:</label>
          <select
            name="rating"
            value={formData.rating}
            onChange={handleChange}
            required
          >
            <option value="0">All</option>
            <option value="1">1 star</option>
            <option value="2">2 stars</option>
            <option value="3">3 stars</option>
            <option value="4">4 stars</option>
            <option value="5">5 stars</option>
          </select>
        </div>
        <button type="submit" className="btn btn-primary" disabled={isLoading}>
          {isLoading ? "Searching..." : "Search"}
        </button>
      </form>

      {error && <div className="error">{error}</div>}

      {searched && (
        <div className="hotels-container">
          {hotels.length === 0 && !isLoading ? (
            <p>No hotels found.</p>
          ) : (
            hotels.map((hotel, index) => (
              <div key={index} className="hotel-card">
                <h3>{hotel.name}</h3>
                <p>Location: {hotel.location}</p>
                <p>Rating: {hotel.rating}</p>
                <button className="btn btn-secondary" onClick={() => handleSaveHotel(hotel)}>
                  Save Hotel
                </button>
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
};

export default SearchHotelPage;
